/**
 * Push Notification Service Worker Extension
 * 
 * This file handles push notification events.
 * It's imported by the main service worker generated by Vite-PWA.
 */

// Handle push notifications
self.addEventListener('push', (event) => {
  console.log('[SW] Push received:', event)
  
  if (!event.data) {
    console.log('[SW] Push event but no data')
    return
  }

  try {
    const data = event.data.json()
    console.log('[SW] Push data:', data)

    const options = {
      body: data.body || 'You have a new notification',
      icon: '/pwa-192x192.png',
      badge: '/pwa-64x64.png',
      vibrate: [100, 50, 100],
      data: {
        url: data.url || '/',
        ...data.data,
      },
      actions: [
        {
          action: 'open',
          title: 'Open',
        },
        {
          action: 'close',
          title: 'Dismiss',
        },
      ],
      // Keep notification until user interacts
      requireInteraction: true,
      // Tag for grouping similar notifications
      tag: data.type || 'general',
      // Renotify even if tag matches
      renotify: true,
    }

    event.waitUntil(
      self.registration.showNotification(data.title || 'Chatravasa', options)
    )
  } catch (err) {
    console.error('[SW] Error parsing push data:', err)
    // Show generic notification if parsing fails
    event.waitUntil(
      self.registration.showNotification('Chatravasa', {
        body: 'You have a new notification',
        icon: '/pwa-192x192.png',
      })
    )
  }
})

// Handle notification click
self.addEventListener('notificationclick', (event) => {
  console.log('[SW] Notification clicked:', event)
  
  event.notification.close()

  if (event.action === 'close') {
    return
  }

  // Get the URL from notification data
  const urlToOpen = event.notification.data?.url || '/'

  event.waitUntil(
    clients.matchAll({ type: 'window', includeUncontrolled: true })
      .then((clientList) => {
        // Check if there's already a window open
        for (const client of clientList) {
          if (client.url.includes(self.location.origin) && 'focus' in client) {
            client.focus()
            // Navigate to the URL
            if (client.navigate) {
              return client.navigate(urlToOpen)
            }
            return client
          }
        }
        // No window open, open a new one
        if (clients.openWindow) {
          return clients.openWindow(urlToOpen)
        }
      })
  )
})

// Handle notification close
self.addEventListener('notificationclose', (event) => {
  console.log('[SW] Notification closed:', event)
})

console.log('[SW] Push notification handler loaded')
